// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract BTCMonInu {
    string public constant name = "BTC Non-Inu";
    string public constant symbol = "BTCMI";
    uint8 public constant decimals = 18;
    uint256 public totalSupply;

    mapping(address => uint256) public balanceOf;

    event Transfer(address indexed from, address indexed to, uint256 value);

    constructor() { // infinite gas 204200 gas
        totalSupply = 21000000000000000000000000;
        // totalSupply = 21000000000000 * 10 ** uint256(decimals);
        balanceOf[msg.sender] = totalSupply;
        emit Transfer(address(0), msg.sender, totalSupply);
    }

    function transfer(address _to, uint256 _value) public returns (bool) {
        require(balanceOf[msg.sender] >= _value, "No balance!");
        balanceOf[msg.sender] = balanceOf[msg.sender] - _value;
        balanceOf[_to] = balanceOf[_to] + _value;
        emit Transfer(msg.sender, _to, _value);
        return true;
    }
}
